element.store:
Ext.define('Me.Netzhirsch.Form', {
extend: 'Ext.data.Store',
fields: [
    { name:'id', type: 'int' },
    { name:'name', type: 'string' }
],
autoLoad: true,
pageSize: 25,
proxy: {
    type: 'ajax',
    url: window.location.href.substr(0
        , window.location.href.indexOf('backend')) + 'backend/PluginConfig/getVariantGroups',
    reader: {
        type: 'json',
        root: 'data',
        totalProperty: 'total'
    }
}
}).create();//new

